    def calculate(self):
    
        try:
            loan_amount = float(self.t1.text())
            interest_rate = float(self.lineEdit_3.text())
            tenure = float(self.t2.text())
            if self.c1.currentText() == "Months":
                tenure_in_months = tenure
            else:
                tenure_in_months = tenure * 12
            monthly_interest_rate = interest_rate / (12 * 100)
            emi = loan_amount * monthly_interest_rate * ((1 + monthly_interest_rate) ** tenure_in_months) / (((1 + monthly_interest_rate) ** tenure_in_months) - 1)
            self.ANSWER.setText(f"â‚¹{emi:.2f}")
        except ValueError:
            self.ANSWER.setText("Invalid input")

    


